UPDATE process_expert
SET pasos = jsonb_set(
    jsonb_set(
        jsonb_set(
            pasos,
            '{1,completado}',
            '{{ $json["pasos.2.completado"] ? "true" : "false" }}'::jsonb,
            false
        ),
        '{1,fecha_completado}',
        '"{{ $json["pasos.2.fecha_completado"] }}"'::jsonb,
        false
    ),
    '{1,datos}',
    to_jsonb(replace('{{ $json["pasos.2.datos"] }}', '"', '\"')), 
    /* to_jsonb(replace(replace('{{ $json["pasos.2.datos"] }}', '"', '\"'), E'\n', ' ')), */
    false
),
paso_actual = {{ $json["paso_actual"] }}
WHERE expert_id = '{{ $json["id"] }}';

